import { Component, AfterViewInit, ViewChild, ElementRef } from '@angular/core';

@Component({
  selector: 'app-transactions-filter-modal',
  templateUrl: './transactions-filter-modal.component.html',
  styleUrls: ['./transactions-filter-modal.component.css']
})
export class TransactionsFilterModalComponent {
  activeLink: string = '';
  activeLinks: string[] = [];
  @ViewChild('minRange') minRangeRef!: ElementRef<HTMLInputElement>;
  @ViewChild('maxRange') maxRangeRef!: ElementRef<HTMLInputElement>;
  @ViewChild('sliderMinValue') minLabelRef!: ElementRef<HTMLElement>;
  @ViewChild('sliderMaxValue') maxLabelRef!: ElementRef<HTMLElement>;
  @ViewChild('rangeHighlight') rangeHighlightRef!: ElementRef<HTMLElement>;

  toggleLink(link: string, event: Event): void {
    event.preventDefault();
    if (this.activeLinks.includes(link)) {
      this.activeLinks = this.activeLinks.filter(l => l !== link);
    } else {
      this.activeLinks.push(link);
    }
  }

  clearLink(link: string, event: Event): void {
    event.preventDefault();
    event.stopPropagation();
    this.activeLinks = this.activeLinks.filter(l => l !== link);
  }

  isActiveLink(link: string): boolean {
    return this.activeLinks.includes(link);
  }
  ngAfterViewInit(): void {
    const minRange = this.minRangeRef.nativeElement;
    const maxRange = this.maxRangeRef.nativeElement;
    const minLabel = this.minLabelRef.nativeElement;
    const maxLabel = this.maxLabelRef.nativeElement;
    const rangeHighlight = this.rangeHighlightRef.nativeElement;

    const updateLabelsAndHighlight = () => {
      const minVal = parseInt(minRange.value, 10);
      const maxVal = parseInt(maxRange.value, 10);
      minLabel.textContent = `$${minVal}`;
      maxLabel.textContent = `$${maxVal}`;

      const minPercent = ((minVal - parseInt(minRange.min, 10)) / (parseInt(minRange.max, 10) - parseInt(minRange.min, 10))) * 100;
      const maxPercent = ((maxVal - parseInt(maxRange.min, 10)) / (parseInt(maxRange.max, 10) - parseInt(minRange.min, 10))) * 100;

      rangeHighlight.style.left = `${minPercent}%`;
      rangeHighlight.style.width = `${maxPercent - minPercent}%`;
    };

    minRange.addEventListener('input', () => {
      if (parseInt(minRange.value, 10) > parseInt(maxRange.value, 10)) {
        minRange.value = maxRange.value;
      }
      updateLabelsAndHighlight();
    });

    maxRange.addEventListener('input', () => {
      if (parseInt(maxRange.value, 10) < parseInt(minRange.value, 10)) {
        maxRange.value = minRange.value;
      }
      updateLabelsAndHighlight();
    });

    updateLabelsAndHighlight(); // التحديث الأولي للتسميات والخلفية
  }
}
<div
  class="modal fade"
  id="transactionsFilterModal"
  tabindex="-1"
  aria-labelledby="exampleModalLabel"
  aria-hidden="true"
>
  <div class="modal-dialog">
    <div class="modal-content d-flex">
      <div class="modal-header p-2 z-1">
        <h1 class="modal-title" id="exampleModalLabel">Filter</h1>
        <button
          type="button"
          class="btn-close"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-body">
        <div class="info d-flex flex-column gap-1">
          <h3>Role</h3>
          <div class="d-flex align-items-center mb-4 gap-2">
            <a
              class="text-decoration-none d-flex align-items-center"
              href="#"
              [ngClass]="{
                breadcrumb: isActiveLink('completed'),
                active: isActiveLink('completed'),
                inActive: !isActiveLink('completed')
              }"
              (click)="toggleLink('completed', $event)"
            >
            Completed
              <span
                *ngIf="isActiveLink('completed')"
                (click)="clearLink('completed', $event)"
                class="clear-link"
              >
                ✖
              </span>
            </a>
            <a
              class="text-decoration-none d-flex align-items-center gap-1"
              href="#"
              [ngClass]="{
                breadcrumb: isActiveLink('pending'),
                active: isActiveLink('pending'),
                inActive: !isActiveLink('pending')
              }"
              (click)="toggleLink('pending', $event)"
            >
            Pending
              <span
                *ngIf="isActiveLink('pending')"
                (click)="clearLink('pending', $event)"
                class="clear-link"
              >
                ✖
              </span>
            </a>
            <a
              class="text-decoration-none d-flex align-items-center gap-1"
              href="#"
              [ngClass]="{
                breadcrumb: isActiveLink('merchants'),
                active: isActiveLink('merchants'),
                inActive: !isActiveLink('merchants')
              }"
              (click)="toggleLink('merchants', $event)"
            >
            Rejected
              <span
                *ngIf="isActiveLink('merchants')"
                (click)="clearLink('merchants', $event)"
                class="clear-link"
              >
                ✖
              </span>
            </a>
          </div>
        </div>
        <div class="info d-flex flex-column">
          <h3 class="m-0">Spent</h3>
          <div class="range-slider">
            <div
              id="range-highlight"
              #rangeHighlight
              class="range-highlight"
            ></div>
            <input
              type="range"
              min="100"
              max="5000"
              value="100"
              id="minRange"
              #minRange
              class="form-range"
            />
            <input
              type="range"
              min="100"
              max="5000"
              value="5000"
              id="maxRange"
              #maxRange
              class="form-range"
            />
          </div>
          <div class="range-labels d-flex justify-content-between">
            <span id="slider-min-value" #sliderMinValue>$100</span>
            <span id="slider-max-value" #sliderMaxValue>$5000</span>
          </div>
        </div>
        <button type="button" class="filter-btn m-auto d-block">Filter</button>
      </div>
    </div>
  </div>
</div>
