import { Component } from '@angular/core';

@Component({
  selector: 'app-request-pagination',
  templateUrl: './request-pagination.component.html',
  styleUrls: ['./request-pagination.component.css']
})
export class RequestPaginationComponent {
  currentPage: number = 1;
  itemsPerPage: number = 5;
  Math = Math;

  toggleAll() {
    this.users.forEach(user => user.selected = this.selectAll);
  }

  checkIfAllSelected() {
    this.selectAll = this.users.every(user => user.selected);
  }

  get paginatedUsers() {
    const start = (this.currentPage - 1) * this.itemsPerPage;
    const end = start + this.itemsPerPage;
    return this.users.slice(start, end);
  }

  setPage(page: number) {
    this.currentPage = page;
  }
}
<nav aria-label="Page navigation example">
  <ul class="pagination">
    <li class="page-item" [class.disabled]="currentPage === 1">
      <a
        class="page-link"
        href="#"
        aria-label="Previous"
        (click)="setPage(currentPage - 1)"
      >
        <span aria-hidden="true">&laquo;</span>
      </a>
    </li>
    <li
      class="page-item"
      *ngFor="
        let page of [].constructor(Math.ceil(users.length / itemsPerPage));
        let i = index
      "
      [class.active]="i + 1 === currentPage"
    >
      <a class="page-link" href="#" (click)="setPage(i + 1)">{{ i + 1 }}</a>
    </li>
    <li
      class="page-item"
      [class.disabled]="currentPage === Math.ceil(users.length / itemsPerPage)"
    >
      <a
        class="page-link"
        href="#"
        aria-label="Next"
        (click)="setPage(currentPage + 1)"
      >
        <span aria-hidden="true">&raquo;</span>
      </a>
    </li>
  </ul>
</nav>
