import { Component } from '@angular/core';

@Component({
  selector: 'app-role-action',
  templateUrl: './role-action.component.html',
  styleUrls: ['./role-action.component.css']
})
export class RoleActionComponent {

}
<div class="search-container d-flex align-items-center position-relative">
  <span class="search-icon p-2">
    <img src="../../../../../assets/img/search-normal.svg" alt="Search Icon" />
  </span>
  <input
    type="text"
    class="form-control search-input"
    placeholder="Search in conversations"
  />
</div>
