import { Component } from '@angular/core';
interface User {
  name: string;
  email: string;
  role: string;
  registered: string;
  address: string;
  phone: string;
  spent: string;
  selected: boolean;
}
@Component({
  selector: 'app-table-of-service',
  templateUrl: './table-of-service.component.html',
  styleUrls: ['./table-of-service.component.css']
})
export class TableOfServiceComponent {
  selectAll: boolean = false;
  users = [
    { id: '20', customerName: 'Mohamede MonGe', requestTitle: 'Survey Report', dateSubmitted: 'September 21, 2013', typeOfService: 'Consolation', status: 'Approved', selected: false },
  ];

  toggleAll() {
    this.users.forEach(user => user.selected = this.selectAll);
  }

  checkIfAllSelected() {
    this.selectAll = this.users.every(user => user.selected);
  }

  toggleUser(user:User) {
    user.selected = !user.selected;
    this.checkIfAllSelected();
  }
}
