import { Componentو } from '@angular/core';

@Component({
  selector: 'app-chat-msg',
  templateUrl: './chat-msg.component.html',
  styleUrls: ['./chat-msg.component.css']
})
export class ChatMsgComponent {
  @ViewChild('scrollContainer') private scrollContainer!: ElementRef;

  messages: string[] = []; // قائمة الرسائل

  constructor() { }

  ngAfterViewChecked() {
    this.scrollToBottom();
  }

  private scrollToBottom(): void {
    const container = this.scrollContainer.nativeElement;
    container.scrollTop = container.scrollHeight;
  }

  addMessage(message: string) {
    this.messages.push(message);
    // بعد إضافة رسالة جديدة، scrollToBottom سيتم استدعاؤه تلقائيًا من ngAfterViewChecked
  }
}
