import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-settings-edit',
  templateUrl: './settings-edit.component.html',
  styleUrls: ['./settings-edit.component.css']
})
export class SettingsEditComponent implements OnInit {
  phoneNumber: string = '+966';
  showPassword = false;
  showNewPassword = false;
  showConfirmPassword = false;

  ngOnInit() {
    const fileInput = document.getElementById('fileInput') as HTMLInputElement;
    const profilePic = document.getElementById('profilePic') as HTMLImageElement;
    const uploadText = document.querySelector('.upload-text') as HTMLHeadingElement;
    const profilePicContainer = document.querySelector('.profile-pic-container') as HTMLDivElement;

    profilePic.addEventListener('click', () => {
      fileInput.click();
    });

    fileInput.addEventListener('change', (event: Event) => {
      const input = event.target as HTMLInputElement;
      if (input.files && input.files[0]) {
        const reader = new FileReader();
        reader.onload = (e: ProgressEvent<FileReader>) => {
          profilePic.src = e.target!.result as string;
          profilePic.style.width = '100%';
          profilePic.style.height = '150px';
          profilePic.style.borderRadius = '50%';
          profilePic.style.marginTop = '0.6rem';
          uploadText.style.display = 'none';
          // حذف الـ display: none; من الـ container لكي يبقى العنصر مرئي
        };
        reader.readAsDataURL(input.files[0]);
      }
    });
  }

  togglePasswordVisibility() {
    this.showPassword = !this.showPassword;
  }

  toggleNewPasswordVisibility() {
    this.showNewPassword = !this.showNewPassword;
  }

  toggleConfirmPasswordVisibility() {
    this.showConfirmPassword = !this.showConfirmPassword;
  }

  onPhoneInput(event: Event) {
    const input = event.target as HTMLInputElement;
    if (!input.value.startsWith('+966')) {
      input.value = '+966';
    }
    if (input.value.length > 13) {
      input.value = input.value.slice(0, 13);
    }
    this.phoneNumber = input.value;
  }
}
<div class="d-flex flex-column">
  <div class="title d-flex flex-column gap-2">
    <h3>Your Profile Picture</h3>
    <div
      class="profile-pic-container position-relative d-flex flex-column align-items-center justify-content-center gap-1"
    >
      <input
        type="file"
        id="fileInput"
        class="filePhoto"
        style="display: none"
      />
      <img
        src="../../../../../assets/img/gallery-add.svg"
        alt="Add Gallery"
        id="profilePic"
      />
      <h5 class="upload-text px-2">Upload Profile Picture</h5>
    </div>
  </div>
  <div class="d-flex flex-column gap-5 mt-5">
    <div class="d-flex gap-4">
      <div class="position-relative input-container">
        <label for="title" class="position-absolute label-style">Name</label>
        <input
          type="text"
          id="title"
          placeholder="Enter Name"
          class="input-style"
        />
      </div>
      <div class="position-relative input-container">
        <label for="phone" class="position-absolute z-1 label-style"
          >Phone Number</label
        >
        <div class="input-wrapper">
          <img
            src="../../../../../assets/img/SA.svg"
            class="flag-icon ps-1 position-absolute"
          />
          <input
            type="text"
            id="phone"
            class="input-style"
            [value]="phoneNumber"
            (input)="onPhoneInput($event)"
            style="padding-left: 2.5rem"
          />
          <div
            class="verify-text position-absolute pe-1"
            data-bs-toggle="modal"
            data-bs-target="#verifyModal"
          >
            Verify
          </div>
        </div>
      </div>
      <div class="position-relative input-container">
        <label for="Email" class="position-absolute label-style">Email</label>
        <input
          type="email"
          id="Email"
          placeholder="Enter Email"
          class="input-style"
        />
      </div>
    </div>
    <div class="d-flex gap-4">
      <div class="position-relative input-container">
        <label for="currentPassword" class="position-absolute label-style"
          >Current Password</label
        >
        <input
          type="{{ showPassword ? 'text' : 'password' }}"
          id="currentPassword"
          placeholder="Enter Current Password"
          class="input-style"
        />
        <i
          class="fa-regular pe-1 {{
            showPassword ? 'fa-eye-slash' : 'fa-eye'
          }} toggle-password-icon"
          (click)="togglePasswordVisibility()"
        ></i>
      </div>
      <div class="position-relative input-container">
        <label for="newPassword" class="position-absolute label-style"
          >New Password</label
        >
        <input
          type="{{ showNewPassword ? 'text' : 'password' }}"
          id="newPassword"
          placeholder="Enter New Password"
          class="input-style"
        />
        <i
          class="fa-regular pe-1 {{
            showNewPassword ? 'fa-eye-slash' : 'fa-eye'
          }} toggle-password-icon"
          (click)="toggleNewPasswordVisibility()"
        ></i>
      </div>
      <div class="position-relative input-container">
        <label for="confirmPassword" class="position-absolute label-style"
          >Confirm Password</label
        >
        <input
          type="{{ showConfirmPassword ? 'text' : 'password' }}"
          id="confirmPassword"
          placeholder="Enter Confirm Password"
          class="input-style"
        />
        <i
          class="fa-regular pe-1 {{
            showConfirmPassword ? 'fa-eye-slash' : 'fa-eye'
          }} toggle-password-icon"
          (click)="toggleConfirmPasswordVisibility()"
        ></i>
      </div>
    </div>
  </div>
  <div
    class="d-flex gap-3 justify-content-end"
    style="margin: 20px 10px 20px 0px"
  >
    <button type="button" class="reject-btn" data-bs-toggle="modal">
      Cancel
    </button>
    <button type="button" class="approve-btn" data-bs-toggle="modal">
      Save
    </button>
  </div>
</div>
