import { Component } from '@angular/core';

@Component({
  selector: 'app-request-filter-modal',
  templateUrl: './request-filter-modal.component.html',
  styleUrls: ['./request-filter-modal.component.css']
})
export class RequestFilterModalComponent {
  activeLink: string = '';

  setActiveLink(link: string, event: Event): void {
    event.preventDefault(); // Prevent default link behavior
    this.activeLink = link;
  }
  ngAfterViewInit(): void {
    const minRange = document.getElementById('minRange') as HTMLInputElement;
    const maxRange = document.getElementById('maxRange') as HTMLInputElement;
    const minLabel = document.getElementById('slider-min-value') as HTMLElement;
    const maxLabel = document.getElementById('slider-max-value') as HTMLElement;

    const updateLabels = () => {
      minLabel.textContent = `$${minRange.value}`;
      maxLabel.textContent = `$${maxRange.value}`;
    };

    minRange.addEventListener('input', () => {
      if (parseInt(minRange.value) > parseInt(maxRange.value)) {
        minRange.value = maxRange.value;
      }
      updateLabels();
    });

    maxRange.addEventListener('input', () => {
      if (parseInt(maxRange.value) < parseInt(minRange.value)) {
        maxRange.value = minRange.value;
      }
      updateLabels();
    });

    updateLabels(); // التحديث الأولي للتسميات
  }
}
<div
  class="modal fade"
  id="filterModal"
  tabindex="-1"
  aria-labelledby="exampleModalLabel"
  aria-hidden="true"
>
  <div class="modal-dialog">
    <div class="modal-content d-flex">
      <div class="modal-header p-3 z-1">
        <h1 class="modal-title" id="exampleModalLabel">Filter</h1>
        <button
          type="button"
          class="btn-close"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-body">
        <div class="info d-flex flex-column gap-1">
          <h3>Role</h3>
          <div class="d-flex align-items-center mb-3">
            <a
              class="text-decoration-none me-3"
              href="#"
              [ngClass]="{
                breadcrumb: activeLink === 'customers',
                active: activeLink === 'customers',
                inActive: activeLink !== 'customers'
              }"
              (click)="setActiveLink('customers', $event)"
            >
              Customers
            </a>
            <a
              class="text-decoration-none me-3"
              href="#"
              [ngClass]="{
                breadcrumb: activeLink === 'provider',
                active: activeLink === 'provider',
                inActive: activeLink !== 'provider'
              }"
              (click)="setActiveLink('provider', $event)"
            >
              Provider
            </a>
            <a
              class="text-decoration-none"
              href="#"
              [ngClass]="{
                breadcrumb: activeLink === 'merchants',
                active: activeLink === 'merchants',
                inActive: activeLink !== 'merchants'
              }"
              (click)="setActiveLink('merchants', $event)"
            >
              Merchants
            </a>
          </div>
        </div>
        <div class="info d-flex flex-column">
          <h3>Spent</h3>
          <div class="range-slider">
            <input type="range" min="100" max="5000" value="100" id="minRange" class="form-range">
    <input type="range" min="100" max="5000" value="5000" id="maxRange" class="form-range">
            <div class="range-labels d-flex justify-content-between mb-3">
              <span>$100</span>
              <span>$5000</span>
            </div>
          </div>
          <button type="button" class="filter-btn m-auto d-block">
            Filter
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
